// import './App.css';
import React, { useState } from 'react';
import About from './components/About'
import Nav from './components/Nav'
import Gallery from './components/Gallery';

function App() {
  // the initial array we will use for useState, it will be defined as the followinf
  const [categories] = useState([
    {
      name: 'commercial',
      description: 'Photos of grocery stores, food trucks, and other commercial projects',
    },
    { name: 'portraits', description: 'Portraits of people in my life' },
    { name: 'food', description: 'Delicious delicacies' },
    { name: 'landscape', description: 'Fields, farmhouses, waterfalls, and the beauty of nature' },
  ]);

  const [currentCategory, setCurrentCategory] = useState(categories[0]); //to useState functions, the object needs to be defined, and then the function, we are going to have currentCategory be defined as our categories

  return (
    // this is JSX, HTML represented in JS, this works due to webpack and React, react basically uses JSX as document.createElement()
    <div>
      <Nav>
        categories={categories}
        setCurrentCategory={setCurrentCategory}
        currentCategory={currentCategory}
      </Nav>
      <main>
        <Gallery></Gallery>
        <About></About>
      </main>
  </div>
  );
}

export default App;
