import React, { useState } from 'react'

function ContactForm() {
    const { name, email, message } = formState;
    //defining the objects in formState

    const [formState, setFormState] = useState({ name: '', email: '', message: '' }); // with this Hook, we can clear the input fields when the component loads from the formState

    function handleChange(e){
        setFormState({...formState, [e.target.name]: e.target.value })
        // ...formState keeps the other key-value pairs for the object...formState, name:....

    }
    console.log(formState)


    return (
        <section>
          <h1>Contact me</h1>
          <form id="contact-form">
            <div>
                <label htmlFor="name">Name:</label>
                <input type="text" name="name" defaultValue={name} onChange={handleChange}/>
            </div>
            <div>
                <label htmlFor="email">Email address:</label>
                <input type="email" name="email" defaultValue={email} onChange={handleChange} />
            </div>
            <div>
                <label htmlFor="message">Message:</label>
                <textarea name="message" rows="5" defaultValue={message} onChange={handleChange}/>
            </div>

            <button type="submit">Submit</button>
          </form>
        </section>
      )

    }

export default ContactForm;
