import React, { useState } from 'react'

function ContactForm() {

    // this first part is setting the form attributes
    const [formState, setFormState] = useState({ name: '', email: '', message: '' }); // with this Hook, we can clear the input fields when the component loads and set formState to have three properties of empty strings

    const { name, email, message } = formState;
    //destructure formState into its properties so that the attribute defaultValue below can be name instead of formState.name

    //grabbing the values next. Form state is the object with clear strings while setFormState will be our new formState object with our new values
    function handleChange(e){
        setFormState({...formState, [e.target.name]: e.target.value })
        // ...formState keeps the other key-value pairs for the object...formState, and name of the name attribute below 
        //(ex: ...formState, email: e.target.value):....

    }
    console.log(formState)

    function handleSubmit(e) {
        e.preventDefault();
        console.log(formState);
      }

    return (
        <section>
          <h1>Contact me</h1>
          <form id="contact-form" onSubmit={handleSubmit}>
            <div>
                <label htmlFor="name">Name:</label>
                <input type="text" name="name" defaultValue={name} onChange={handleChange}/>
            </div>
            <div>
                <label htmlFor="email">Email address:</label>
                <input type="email" name="email" defaultValue={email} onChange={handleChange} />
            </div>
            <div>
                <label htmlFor="message">Message:</label>
                <textarea name="message" rows="5" defaultValue={message} onChange={handleChange}/>
            </div>

            <button type="submit">Submit</button>
          </form>
        </section>
      )

    }

export default ContactForm;

