import React, { useEffect } from 'react'
import { capitalizeFirstLetter } from "../../utils/helpers"

function Nav(props){
    //using JS here to define our array objects that we would like to display in our React component
    const {
        categories = [],
        setCurrentCategory,
        currentCategory,
      } = props;

    useEffect(() => {
        document.title = capitalizeFirstLetter(currentCategory.name);
    }, [currentCategory]); // first arguement is callback function, second directs hook to re-render component on any changes.

    return (
        <header className="flex-row px-1">
          <h2>
            <a data-testid="link" href="/">
              <span role="img" aria-label="camera">
                {" "}
